'''实现一个简单的二维向量类'''from math import hypotclass Vector:    def __init__(self, x = 0, y = 0):        self.x = x        self.y = y        pass    # 输出运算符重载    def __repr__(self):        return "Vector({}, {})".format(self.x, self.y)        pass    def __abs__(self):        return hypot(self.x, self.y)        pass    def __bool__(self):        return bool(abs(self))        pass    def __add__(self, other):        x = self.x + other.x        y = self.y + other.y        return Vector(x, y)        pass    def __mul__(self, scalar):        return Vector(self.x * scalar, self.y * scalar)        pass    passif __name__ == '__main__':    # 定义个向量    v1 = Vector(2, 4)    v2 = Vector(2, 1)    # 加法和输出运算符    print(v1 + v2)    print(abs(v1))    # * 法重载    print(v1 * 3)    pass